mapper.select:
SELECT * FROM AgendaAndDebateBoardCommet
  WHERE articleIdx = #{articleIdx}
    ORDER BY createDate DESC;

mapper.insert:
INSERT INTO AgendaAndDebateBoardCommet
(
articleIdx
, userIdx
, type
, comment
, good
, bad
, createdate
, updatedate
)
VALUES (
#{articleIdx}
, #{userIdx}
, #{type}
, #{comment}
, #{good}
, #{bad}
, CURRENT_TIMESTAMP()
, CURRENT_TIMESTAMP()
)
