mapper.insert:
INSERT INTO BASICBOARDARTICLE
    (
      ARTICLEIDX
    , bad
    , content
    , createdate
    , good
    , subboardidx
    , title
    , updatedate
    , useridx
    , usernickname
    )
VALUES (
      (select ifnull(max(a.ARTICLEIDX), 0)+1 from BASICBOARDARTICLE a)
    , #{bad}
    , #{content}
    , CURRENT_TIMESTAMP()
    , #{good}
    , #{subBoardIdx}
    , #{title}
    , CURRENT_TIMESTAMP()
    , #{userIdx}
    , #{userNickName}
    )

mapper.update:
UPDATE
      BASICBOARDARTICLE
SET  title = #{title}
    , content = #{content}
    , updateDate = CURRENT_TIMESTAMP()
    , userNickName = #{userNickName}
WHERE articleIdx = #{articleIdx}
