mapper.insert:
INSERT INTO USER
    (
    userIdx,
    userId,
    userNickName,
    password,
    experience,
    tendency,
    introduction,
    authSnsId,
    createDate,
    updateDate
    )
VALUES (
      (select ifnull(max(a.USERIDX), 0)+1 from USER a)
    , #{userId}
    , #{userNickName}
    , #{password}
    , #{experience}
    , #{tendency}
    , #{introduction}
    , #{authSnsId}
    , CURRENT_TIMESTAMP()
    , CURRENT_TIMESTAMP()
    )

mapper.update:
UPDATE
      USER
SET password = #{encodedPassword}
WHERE userIdx = #{userIdx}

 UPDATE
      USER
SET userNickName = #{userNickName}
WHERE userIdx = #{userIdx}
